(37, 'wc-admin-set-up-additional-payment-types', 'info', 'en_US', 'Configurer des fournisseurs de paiement supplémentaires', 'Configurer des moyens de paiement additionnels, en utilisant des services tiers ou autres méthodes.', '{}', 'unactioned', 'woocommerce-admin', '2021-08-02 14:43:10', NULL, 0, 'plain', '', 0, 'info'),
(38, 'wc-payments-notes-set-up-refund-policy', 'info', 'en_US', 'Set up refund policy', 'Protect your merchant account from fraudulent disputes by defining the policy and making it accessible to customers.', '{}', 'unactioned', 'woocommerce-payments', '2021-08-02 14:43:41', NULL, 0, 'plain', '', 0, 'info'),
